<span class="line"><span style="color:#C792EA;">const</span><span style="color:#A6ACCD;"> immutableString </span><span style="color:#89DDFF;">=</span><span style="color:#A6ACCD;"> </span><span style="color:#89DDFF;">&#39;</span><span style="color:#C3E88D;">immutableString</span><span style="color:#89DDFF;">&#39;</span><span style="color:#89DDFF;">;</span></span>
