import{_ as e,o as t,c as a,V as r}from"./chunks/framework.b450deef.js";const m=JSON.parse('{"title":"","description":"","frontmatter":{},"headers":[],"relativePath":"articles/02-框架/vue/路由页面历史栈的处理.md","filePath":"articles/02-框架/vue/路由页面历史栈的处理.md"}'),o={name:"articles/02-框架/vue/路由页面历史栈的处理.md"},p=r('<h2 id="运行时注册路由" tabindex="-1">运行时注册路由 <a class="header-anchor" href="#运行时注册路由" aria-label="Permalink to &quot;运行时注册路由&quot;">​</a></h2><p>不能每一个页面对应一个路由配置 因为存在一个路由配置对应不同参数的页面,这个时候addRoute应该会覆盖之前的</p><p>因此动态注册路由应该以包的维度全量注册</p><p>并且路由信息上的meta就不能作为唯一页面的参数</p><p>因此时间戳不能存在路由信息里</p><p>那么物理返回前进，用beforeach 怎么判断是前面的页面 只知道to、from</p><p>list -&gt; detail -&gt; list</p><p>detail -&gt; list 👆 有可能是前进也可能是返回</p><p>本来想法是时间戳存在每个路由的meta里，就能多知道路由的信息 但是发现好像相同的路由addroute是覆盖而不是新增一个唯一的route (name不相同好像可以做到重复路由的配置)</p><p>但是name要在api跳转的时候用到，api跳转时并不知道时间戳</p><p>好像是name的时间戳维护addroute的有问题</p><p><a href="https://github.com/hezhongfeng/vue-page-stack/blob/master/src/index.js" target="_blank" rel="noreferrer">别人实现的vue单页历史栈1</a></p><p><a href="https://github.com/luojilab/vue-stack-router" target="_blank" rel="noreferrer">别人实现的vue单页历史栈2</a></p>',13),_=[p];function s(i,n,c,l,d,u){return t(),a("div",null,_)}const f=e(o,[["render",s]]);export{m as __pageData,f as default};
